UPDATE TBLMAUDITACTIVITY SET MESSAGE = 'SNMP Alert status updated for ServerInstance $serverInstanceName' WHERE ALIAS = 'UPDATE_SNMP_ALERT_STATUS';
UPDATE TBLMAUDITACTIVITY SET  ALIAS='UPDATE_GENERAL_PARAMETER' WHERE ALIAS = 'UPDATE_SYSTEM_PARAMETER';
UPDATE TBLMAUDITENTITY SET ALIAS='RULE DATA DICTIONARY' WHERE ALIAS = 'RULE LOOKUP MANAGER';
UPDATE TBLMAUDITSUBENTITY SET ALIAS= 'RULE_DATA_DICTIONARY' WHERE ALIAS = 'RULE_LOOKUP_MANAGER';
UPDATE TBLMAUDITACTIVITY SET ALIAS = 'UPDATE_CONSOLIDATION_DEFINITION',MESSAGE = 'Consolidation Definition $entityName updated in service $serviceName.' WHERE ALIAS = 'UPDATE_CONSOLIDATION_DEFINATION';
UPDATE TBLMAUDITACTIVITY SET ALIAS = 'DELETE_CONSOLIDATION_DEFINITION',MESSAGE = 'Consolidation Definition $entityName deleted in service $serviceName.' WHERE ALIAS = 'DELETE_CONSOLIDATION_DEFINATION';
UPDATE TBLMAUDITACTIVITY SET ALIAS = 'CREATE_CONSOLIDATION_DEFINITION',MESSAGE = 'Consolidation Definition $entityName created in service $serviceName.' WHERE ALIAS = 'CREATE_CONSOLIDATION_DEFINATION';
UPDATE TBLMAUDITACTIVITY SET ALIAS = 'CREATE_DATABASE_DISTRIBUTION_DRIVER_ATTRIBUTE' WHERE ALIAS='CREATE_DATABASE_DISTRIBUTION_DRIVER_ATTTRIBUTE';
UPDATE TBLMAUDITACTIVITY SET ALIAS= 'DELETE_DATABASE_DISTRIBUTION_DRIVER_ATTRIBUTE' WHERE ALIAS= 'DELETE_DATABASE_DISTRIBUTION_DRIVER_ATTTRIBUTE';
UPDATE TBLMAUDITACTIVITY SET ALIAS= 'UPDATE_DATABASE_DISTRIBUTION_DRIVER_ATTRIBUTE' WHERE ALIAS= 'UPDATE_DATABASE_DISTRIBUTION_DRIVER_ATTTRIBUTE';
UPDATE TBLMAUDITACTIVITY SET SUBENTITYID = 18 WHERE ALIAS = 'UPDATE_IPLOG_PATHLIST';
UPDATE TBLMAUDITACTIVITY SET SUBENTITYID = 18 WHERE ALIAS = 'DELETE_PARSING_PATHLIST';
UPDATE TBLMAUDITACTIVITY SET SUBENTITYID = 16 WHERE ALIAS = 'UPDATE_PARSER_MAPPING_DETAILS';
DELETE FROM TBLTSYSTEMAUDITDETAIL WHERE FIELDNAME = 'destCharset';
DELETE FROM TBLTSYSTEMAUDITDETAIL WHERE FIELDNAME = 'destDateFormat';
DELETE FROM TBLTSYSTEMAUDITDETAIL WHERE FIELDNAME = 'destFileExt';
DELETE FROM TBLTSYSTEMAUDITDETAIL WHERE FIELDNAME = 'destCharset';
DELETE FROM TBLTSYSTEMAUDITDETAIL WHERE FIELDNAME = 'destDateFormat';
DELETE FROM TBLTSYSTEMAUDITDETAIL WHERE FIELDNAME = 'destFileExt';
UPDATE TBLTSYSTEMAUDIT SET STATUS  = 'DELETE' WHERE ACTIVITYID = (SELECT ID FROM TBLMAUDITACTIVITY WHERE SUBENTITYID = 16 AND ALIAS = 'UPDATE_COMPOSER_MAPPING_DETAIL');
UPDATE TBLMAUDITACTIVITY SET ALIAS = 'UPDATE_COMPOSER_MAPPING_DETAIL_DELETED' WHERE SUBENTITYID = 16 AND ALIAS = 'UPDATE_COMPOSER_MAPPING_DETAIL';
UPDATE TBLMAUDITACTIVITY SET ALIAS='UPDATE_COMPOSER_BASIC_MAPPING_DETAIL' WHERE ALIAS = 'UPDATE_COMPOSER_MAPPING_DETAIL';

