UPDATE TBLTPATHLIST SET REFERENCEDEVICE = (SELECT TBLMDEVICE.NAME FROM TBLMDEVICE WHERE ID = (SELECT MAX(ID) FROM TBLMDEVICE)), 
						PARENTDEVICE=(SELECT ID FROM TBLMDEVICE WHERE ID = (SELECT MAX(ID) FROM TBLMDEVICE)) 
WHERE REFERENCEDEVICE IS NULL;

UPDATE TBLTPATHLIST SET REFERENCEDEVICE = (SELECT NAME FROM TBLMDEVICE WHERE ID = (SELECT MAX(ID) FROM TBLMDEVICE as id)), 
						PARENTDEVICE=(SELECT ID FROM TBLMDEVICE WHERE ID = (SELECT MAX(ID) FROM TBLMDEVICE as id)) 
WHERE REFERENCEDEVICE IS NULL;

