UPDATE TBLMBUSINESSSUBMODULE set status = 'INACTIVE' where ID in (SELECT id FROM TBLMBUSINESSSUBMODULE s WHERE PARENTBUSINESSMODULEID=15 and ALIAS not in ('DYNAMIC_REPORT'));

DELETE from TBLTACCESSGROUPACTIONREL WHERE ACTIONID in 
	(SELECT id FROM TBLMACTION WHERE PARENTBUSINESSSUBMODULEID in 
		(SELECT id FROM TBLMBUSINESSSUBMODULE WHERE PARENTBUSINESSMODULEID=15 and ALIAS not in ('DYNAMIC_REPORT')));

UPDATE TBLMBUSINESSSUBMODULE as t1,(SELECT id FROM TBLMBUSINESSSUBMODULE WHERE PARENTBUSINESSMODULEID=15 and ALIAS not in ('DYNAMIC_REPORT')) as t2 set status = 'INACTIVE' where t1.id = t2.id;
DELETE from TBLTACCESSGROUPACTIONREL WHERE ACTIONID in 
	(SELECT id FROM TBLMACTION WHERE PARENTBUSINESSSUBMODULEID in 
		(SELECT id FROM TBLMBUSINESSSUBMODULE WHERE PARENTBUSINESSMODULEID=15 and ALIAS not in ('DYNAMIC_REPORT')));

