UPDATE TBLMSYSTEMPARAMETER SET NAME='TLS Authentication', ENABLED = 'Y', VALUE = 'true' WHERE ALIAS = 'AUTHENTICATION_TYPE';
UPDATE TBLMSYSTEMPARAMETER SET ENABLED = 'N', VALUE = 'true' WHERE ALIAS = 'SMTP_USERNAME';
UPDATE TBLMSYSTEMPARAMETER SET ENABLED = 'N', VALUE = 'true' WHERE ALIAS = 'SMTP_USER_PASSWORD';

