UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLTSNMPALERT) WHERE TABLE_NAME='SNMPAlert';

UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) as num FROM TBLTCOMPOSERATTR) WHERE TABLE_NAME='ComposerAttribute'; 
UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) as num FROM TBLMREGEX) WHERE TABLE_NAME='EntitiesRegex'; 
UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) as num FROM TBLTSNMPALERT) WHERE TABLE_NAME='SNMPAlert';

UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLTCOMPOSERATTR as t) WHERE TABLE_NAME='ComposerAttribute'; 
UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLMREGEX as t) WHERE TABLE_NAME='EntitiesRegex'; 
UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLTSNMPALERT as t) WHERE TABLE_NAME='SNMPAlert';

UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLMDEVICETYPE) WHERE TABLE_NAME='DeviceType';  
UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLMDEVICE) WHERE TABLE_NAME='Device';  
UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLMVENDORTYPE) WHERE TABLE_NAME='VendorType';

