UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) as num FROM TBLMDEVICE) WHERE TABLE_NAME='Device';
UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) as num FROM TBLMVENDORTYPE) WHERE TABLE_NAME='VendorType';

