UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLMDEVICE as t) WHERE TABLE_NAME='Device';
UPDATE TBLTPRIMARYKEY SET VALUE=(SELECT (MAX(ID)+1) FROM TBLMVENDORTYPE as t) WHERE TABLE_NAME='VendorType';
